
var request = require('request');

module.exports = function(appId, callback) {
    var getUrl = 'http://fir.im/api/version' + '?appid=' + appId;

    console.log('Will get app info from: '.green + getUrl.underline);
    request(getUrl, function (error, response, body) {
        if (!error && response.statusCode == 200) {
            console.log(('\nThe ' + appId + ' info is:').green)

            var json = JSON.parse(body);
            for (var k in json) {
                console.log('\t' + k.green + "\t\t\t: " + json[k]);
            }
             // Print the google web page.
            callback(0);
        } else {
            callback(1, error.toString());
        }
    })

}